import React from 'react';
import { Route, Switch, Link } from 'react-router-dom';

// Import the Components
import TodoList from './components/TodoList';
import EditItem from './components/EditItem';
import AddItem from './components/AddItem';


const App = () => {

  return(
    <div>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <Link className="navbar-brand h1" to="/">To-Do App</Link>
          <ul className="navbar-nav d-flex flex-row align-item mr-5">
              <li className="nav-item mr-3">
                <Link className="nav-link" aria-current="page" to="/">Home</Link>
              </li>
              <li className="nav-item px-3 py-0  btn btn-primary">
                <Link className="nav-link active" href="/add-item">Add</Link>
              </li>
            </ul>
        </div>
      </nav>
      <Switch>
        <Route exact path="/" component={TodoList}/>
        <Route path="/edit/:id" component={EditItem}/>
        <Route path="/add-item" component={AddItem}/>
      </Switch>
    </div>
  )
}

export default App;
